import React, { useState, useEffect } from 'react'
import Checkbox from '@material-ui/core/Checkbox';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import Input from '@material-ui/core/Input';    
import ListItemText from '@material-ui/core/ListItemText';
import Container from '@material-ui/core/Container';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import './css/header.css'




export default function Header() {
    const [data, setData] = useState([]);
    const [choose, setChoose] = useState([])

    useEffect(() => {
        fetch('http://www.mocky.io/v2/5c9105cb330000112b649af8')
        .then(res =>{
            return res.json()
        })
        .then(response =>{
            setData(response.furniture_styles)
        })
        }, [])


        const optionSelect = data.map((index, item) =>{
            return(
                <MenuItem key={index} value={item}>
                    <Checkbox value="uncontrolled" checked={choose.indexOf(index) > -1} />
                    <ListItemText primary={item} />
                </MenuItem>
            )
        })

        const handleChange = event => {
            setChoose(event.target.value)
        }

        
        console.log(choose)

    return (
        <div>
            <Container fixed>

                

                <FormControl className="form-container">
                <InputLabel id="demo-mutiple-checkbox-label">Furtniture Style</InputLabel>
                    <Select 
                        labelId="demo-mutiple-checkbox-label"
                        multiple
                        value ={choose}
                        input={<Input />}
                        onChange={handleChange}   
                        renderValue={selected => selected.join(', ')}
                        >
                        {optionSelect}
                    </Select>
                </FormControl>



            </Container>
        </div>
    )
}
